#include "pch.h"
#include "../BinaryTranslator/BinaryTranslator.h"


//Adam Tests
TEST(BinaryTranslator, TxtToBin) {
	BinaryTranslator test;
	string expected = "010010000110010101101100011011000110111100100000010101110110111101110010011011000110010000100001";
	EXPECT_EQ(expected, test.TextToBinary("Hello World!"));
	
	expected = "";
	EXPECT_EQ(expected, test.TextToBinary("The quick brown fox jumps over the lazy dog"));
}

TEST(BinaryTranslator, HexToTxt) {
	BinaryTranslator test;
	string expected = "48656c6c6f20576f726c6421";
	EXPECT_EQ(expected, test.HexToText("Hello World!"));
	
	expected = "54686520717569636b2062726f776e20666f78206a756d7073206f76657220746865206c617a7920646f67";
	EXPECT_EQ(expected, test.HexToText("The quick brown fox jumps over the lazy dog"));
}

//Tyler Tests

TEST(BinaryTranslator, BinToTxt) {
	BinaryTranslator test;
	string input = "010010000110010101101100011011000110111100100000010101110110111101110010011011000110010000100001";
	EXPECT_EQ("Hello World!", test.BinaryToText(input));

	input = "";
	EXPECT_EQ("The quick brown fox jumps over the lazy dog", test.BinaryToText(input));
}

TEST(BinaryTranslator, TxtToHEx) {
	BinaryTranslator test;
	string input = "48656c6c6f20576f726c6421";
	EXPECT_EQ("Hello World!", test.HexToText(input));

	input = "54686520717569636b2062726f776e20666f78206a756d7073206f76657220746865206c617a7920646f67";
	EXPECT_EQ("The quick brown fox jumps over the lazy dog", test.HexToText(input));
}

int main(int argc, char** argv) {
	return RUN_ALL_TESTS();
}